// WORKS, BUT EXCEEDES THE TIME LIMIT ON LEETCODE

/**
 * @param {string} s
 * @param {string} t
 * @return {string}
 */
var minWindow = function(s, t) {

    let length = 9999999
    let output_substr = ''
    let str = s
    while (str.length >= t.length) {

        let coords = findSubstr(str, t)
        if (coords !== false) {

            let substr = str.slice(coords[0], coords[1]+1)
            if (substr.length < length) {
                length = substr.length
                output_substr = substr
            }
            if (coords[0] > 0) {
                str = str.slice(coords[0])
            } else {
                str = str.slice(1)
            }
        } else {
            break;
        }



    }

    return output_substr
};

function findSubstr(s, t) {
    let indices = []
    for(let i = 0; i < t.length; i++) {
        let char = t[i]

        let char_idx = findIndex(s, char, indices, 0)
        if (char_idx !== -1) {
            indices.push(char_idx)
        } else {
            return false
        }
    }

    let min = Math.min(...indices);
    let max = Math.max(...indices);

    return [min, max]
}

function findIndex(s, char, indices, offset) {
    let idx = s.indexOf(char, offset)
    if (idx !== -1) {
        if (indices.indexOf(idx) !== -1) {
            idx = findIndex(s, char, indices, idx + 1)
        } 
    }

    return idx
}


